// -----------------------------------------------------------------------------
// Token types
// -----------------------------------------------------------------------------
export type AccessToken = string;

// -----------------------------------------------------------------------------
// Request payloads
// -----------------------------------------------------------------------------
export type LoginRequest = {
    email: string;
    password: string;
};

// -----------------------------------------------------------------------------
// Response payloads
// -----------------------------------------------------------------------------
export type LoginResponse = {
    accessToken: AccessToken;
};

export type RefreshResponse = {
    accessToken: AccessToken;
};
